def get_hash():
    """获取随机字符串"""
    letters = string.ascii_letters
    print(letters)
    rand = ''.join(random.sample(letters, 10))
    print(type(rand))
    hash = hashlib.md5(rand.encode()).hexdigest()
    return hash
